import type {FC} from 'react';
import {useState, useEffect} from 'react';
import {getCooks, addCook} from './utils';
import {Card, Avatar, Button, Dialog, DialogTitle, TextField, Select, MenuItem, Collapse, IconButton} from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';
import NavBar from './components/navBar';
import './styles/index.css';


const Page:FC = ()=>{
    const [cooks, setCooks]:any = useState ([]);
    const [open, setOpen] = useState (false);

    const handleCloseDialog = ()=>setOpen (false);

    useEffect(()=>{ 
        getCooks ().then(cooks=>setCooks (cooks));

    }, [])

    return (
        <div className="admin-login">
            <NavBar />

            <main className="admin-main-container">
                <Button onClick={()=>setOpen (!open)} style={{backgroundColor: "blue"}}>Add Cook</Button>

                <div className="ad-card-cooks">
                    {
                        cooks.map((cook:any)=><Carder name={cook.Name} rating={cook.Rating} image={cook.Profile_Image} region={cook.Region} state={cook.State}/>)
                    }
                </div>



            </main>
            <DialogBox open={open} handleCloseDialog={handleCloseDialog} />
        </div>
    )
}


export default Page;

interface CarderType {
    image: string,
    name: string,
    rating: string,
    region: string,
    state: string
}


const useCarderStyles = makeStyles ({ 
    root: {
        marginBottom: 10
    }
})

const Carder:FC<CarderType> = ({name, rating, region, state, image})=>{

    const styles = useCarderStyles ();

    return (
        <Card>

            <div className="ad-rows">
            <div className="ad-cooks-name">
                {name}
            </div>

            <Avatar src={image} alt={name}/>
            </div>

            <div className="ad-rows">
                <div className="ad-ratings">
                    Ratings: {rating}
                </div>

                <div className="ad-state">
                    State: {state}
                </div>
            </div>

            <div className="ad-rows">
                <div className="ad-ratings">
                    Region: {region}
                </div>

            </div>




        </Card>
    )
}

interface DialogBoxTypes {
    open: any,
    handleCloseDialog: any
}


const DialogBox:FC<DialogBoxTypes> = ({open, handleCloseDialog})=> {

    const [name, setName] = useState ('');
    const [state, setState] = useState ('');
    const [region, setRegion] = useState ('');
    const [image, setImage] = useState ('');
    const [ratings, setRatings] = useState (0);
    const [speciality, setSpeciality] = useState ('');
    const [years, setYears] = useState ("");
    const [menu, setMenu]:any = useState ({
        Sunday: {
            Dal: "Suhan",
            Non_Veg_Curry: "",
            Raita_Salad: "",
            Rice: "",
            Roti: "",
            Sabji: "",
            Veg_Curry: ""
        },
        Monday: {
            Dal: "",
            Non_Veg_Curry: "",
            Raita_Salad: "",
            Rice: "",
            Roti: "",
            Sabji: "",
            Veg_Curry: ""
        },
        Tuesday: {
            Dal: "",
            Non_Veg_Curry: "",
            Raita_Salad: "",
            Rice: "",
            Roti: "",
            Sabji: "",
            Veg_Curry: ""
        },
        Wednesday: {
            Dal: "",
            Non_Veg_Curry: "",
            Raita_Salad: "",
            Rice: "",
            Roti: "",
            Sabji: "",
            Veg_Curry: ""
        },
        Thursday: {
            Dal: "",
            Non_Veg_Curry: "",
            Raita_Salad: "",
            Rice: "",
            Roti: "",
            Sabji: "",
            Veg_Curry: ""
        },
        Friday: {
            Dal: "",
            Non_Veg_Curry: "",
            Raita_Salad: "",
            Rice: "",
            Roti: "",
            Sabji: "",
            Veg_Curry: ""
        },
        Saturday: {
            Dal: "",
            Non_Veg_Curry: "",
            Raita_Salad: "",
            Rice: "",
            Roti: "",
            Sabji: "",
            Veg_Curry: ""
        }
    });

    const [items, setItems]:any = useState ({
        Dal: "",
        Non_Veg_Curry: "",
        Raita_Salad: "",
        Rice: "",
        Roti: "",
        Sabji: "",
        Veg_Curry: ""
    });

    const [day, setDay]:any = useState ("Sunday"); 
    const [collapse, setCollapse] = useState (false);

    const handleAddMenu = ()=>{
        var Menu = menu;
        Menu [day] = items;
        setMenu (Menu);
        setItems ({
            Dal: "",
            Non_Veg_Curry: "",
            Raita_Salad: "",
            Rice: "",
            Roti: "",
            Sabji: "",
            Veg_Curry: ""
        });
    }




    const useStyles = makeStyles ({



    });

    return (

        <Dialog open={open} onClose={handleCloseDialog} >

            <Card>
                <DialogTitle>Add Cook</DialogTitle>

                <form className="add-cooks-form" noValidate onSubmit={(e)=>{
                        e.preventDefault ();
                        addCook ({
                            Name: name,
                            Region: region,
                            Profile_Image: image,
                            Ratings: ratings,
                            Speciality: speciality,
                            Years_Of_Experience: years
                        })
